def check_emoticons(client, cmd):
    if cmd == "shrug":
        return "¯\_(ツ)_/¯"
    elif cmd == "tableflip":
        return "(╯°□°）╯︵ ┻━┻"
    elif cmd == "unflip":
        return "┬──┬ ノ( ゜-゜ノ)"
    elif cmd == "zoidberg":
        return "(/) (°,,°) (/)"
    elif cmd == "lenny":
        return "( ͡° ͜ʖ ͡°)"
    elif cmd == "lennyx5":
        return "( ͡°( ͡° ͜ʖ( ͡° ͜ʖ ͡°)ʖ ͡°) ͡°)"
    elif cmd == "glasses":
        return "(•_•) ( •_•)>⌐■-■ (⌐■_■)"
    elif cmd == "walking_my_mods":
        return "⌐( ͡° ͜ʖ ͡°) ╯╲___卐卐卐卐"

